library('RankAggreg')
set.seed(100)
aggreg_mlt <- function(m, n) {
  mm <- as.matrix(m)
  t <- RankAggreg(mm, n, method="CE", distance="Spearman",
                  N=110, convIn=5, rho=.1, verbose=FALSE)
  return(t[1])
}

